import React from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { searchPro } from "../redux/products/productSlice";
const SearchInput = () => {
  const [values, setValues] = useSearch();
  const navigate = useNavigate();
  const dispatch = useDispatch()
  const handleSubmit = async (e) => {
    // e.preventDefault();
    // try {
    //   const { data } = await axios.get(
    //     `/products/search/${values.keyword}` // gọi request search
    //   );
    //   setValues({ ...values, results: data }); // kết quả search sẽ được lưu ở context search 
    //   navigate("/search"); // khi search thì sẽ chuyển sang trang và hiển thị các san phẩm bên search
    // } catch (error) {
    //   console.log(error);
    // }
    let data = values.keyword
    dispatch(searchPro(data))
  };
  return (
    <div>
      <form
        className="d-flex search-form"
        role="search"
        onSubmit={handleSubmit}
      >
        <input
          className="form-control me-2"
          type="search"
          placeholder="Search"
          aria-label="Search"
          value={values.keyword}
          onChange={(e) => setValues({ ...values, keyword: e.target.value })}
        />
        <button className="btn btn-outline-success" type="submit"
          onClick={()=>{
            // navigate('/search')
          }}
        >
          Search
        </button>
      </form>
    </div>
  );
};

export default SearchInput;
