import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Checkbox, Radio } from 'antd';
import Product from '../components/Product';
import { getAllCategory } from '../redux/category/categorySlice';
import { getProducts } from '../redux/products/productSlice';

const HomePage = () => {
    const dispatch = useDispatch();
    const [checkedCategories, setCheckedCategories] = useState([]); // State để lưu các ID của category được chọn
    const [selectedPriceRange, setSelectedPriceRange] = useState(''); // State để lưu giá trị của price range được chọn
    const products = useSelector(state => state.product.products); // Lấy danh sách sản phẩm từ Redux store

    useEffect(() => {
        // Gọi action để lấy tất cả các category và sản phẩm từ Redux store khi component được render
        dispatch(getAllCategory());
        dispatch(getProducts());
    }, [dispatch]); // Thêm dispatch vào dependencies để tránh warning

    // Hàm xử lý khi checkbox thay đổi
    const handleCategoryChange = (id, checked) => {
        // Cập nhật state checkedCategories dựa trên thay đổi của checkbox
        if (checked) {
            setCheckedCategories([...checkedCategories, id]);
        } else {
            setCheckedCategories(checkedCategories.filter(categoryId => categoryId !== id));
        }
    };

    // Hàm xử lý khi radio button thay đổi
    const handlePriceRangeChange = (e) => {
        // Cập nhật state selectedPriceRange dựa trên thay đổi của radio button
        setSelectedPriceRange(e.target.value);
    };

    // Lọc sản phẩm dựa trên category và price range được chọn
    const filteredProducts = products.filter(product => {
        // Kiểm tra xem sản phẩm có trong danh sách category được chọn hay không
        const categoryMatch = checkedCategories.length === 0 || checkedCategories.includes(product.category);
        // Kiểm tra xem sản phẩm có nằm trong price range được chọn hay không
        const priceMatch = selectedPriceRange === '' || product.price <= selectedPriceRange;
        // Trả về true nếu sản phẩm thỏa mãn cả hai điều kiện trên
        return categoryMatch && priceMatch;
    });

    // Render UI của component
    return (
        <div className='container'>
            <div className='row'>
                <div className='col-md-3'>
                    <h4>Filter By Category</h4>
                    <div>
                        {/* Render danh sách checkbox để chọn category */}
                        {categories.map(category => (
                            <Checkbox key={category.id} onChange={(e) => handleCategoryChange(category.id, e.target.checked)}>
                                {category.name}
                            </Checkbox>
                        ))}
                    </div>
                    <h4>Filter By Price</h4>
                    <div>
                        {/* Render danh sách radio button để chọn price range */}
                        {priceRanges.map(priceRange => (
                            <Radio key={priceRange.id} value={priceRange.value} onChange={handlePriceRangeChange}>
                                {priceRange.label}
                            </Radio>
                        ))}
                    </div>
                </div>
                <div className='col-md-9'>
                    <h4>All Products</h4>
                    <div>
                        {/* Render danh sách sản phẩm sau khi lọc */}
                        {filteredProducts.map(product => (
                            <Product key={product.id} product={product} />
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
