import React, { useEffect, useState } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Autoplay } from 'swiper/modules';
import { Checkbox, Radio } from 'antd';
import { PriceData } from '../utils/PriceData';
import { useDispatch, useSelector } from 'react-redux';
import { getAllCategory } from '../redux/category/categorySlice';
import { getListProducts, getProducts, getTotal } from '../redux/products/productSlice';
import Product from '../components/Product';
import axios from 'axios';

const ListImgSlider = [
    { img: "https://scr.vn/wp-content/uploads/2020/07/T%E1%BA%A3i-h%C3%ACnh-%E1%BA%A3nh-si%C3%AAu-xe-HD-c%E1%BB%B1c-%C4%91%E1%BA%B9p-v%E1%BB%81-m%C3%A1y.jpg" },
    { img: "https://scr.vn/wp-content/uploads/2020/07/%E1%BA%A2nh-si%C3%AAu-xe-Lamborghini-Full-HD.jpg" },
    { img: "https://scr.vn/wp-content/uploads/2020/07/%E1%BA%A2nh-si%C3%AAu-xe-%C4%91%E1%BA%B9p-ch%E1%BA%A5t-l%C6%B0%E1%BB%A3ng-cao.jpg" },
    { img: "https://scr.vn/wp-content/uploads/2020/07/H%C3%ACnh-Si%C3%AAu-xe-Roll-Royce-4k-scaled.jpg" },
]

const HomePage = () => {
    const dispatch = useDispatch();
    const [checkedCategories, setCheckedCategories] = useState([]); // State to hold selected category IDs
    const [selectedPriceRange, setSelectedPriceRange] = useState(''); // State to hold selected price range
    const products = useSelector(state => state?.product?.products);

    useEffect(() => {
        dispatch(getAllCategory());
        dispatch(getProducts());
    }, []);

    // Filter products based on selected checkboxes and radio button
    const filteredProducts = products.filter(product => {
        // Filter by selected categories
        const categoryMatch = checkedCategories.length === 0 || checkedCategories.includes(product.category);
        // Filter by selected price range
        const priceMatch = selectedPriceRange === '' || product.price <= selectedPriceRange;
        return categoryMatch && priceMatch;
    });

    const handleCategoryChange = (id, checked) => {
        // Update selected categories state based on checkbox change
        if (checked) {
            setCheckedCategories([...checkedCategories, id]);
        } else {
            setCheckedCategories(checkedCategories.filter(categoryId => categoryId !== id));
        }
    };

    const handlePriceRangeChange = (e) => {
        // Update selected price range state based on radio button change
        setSelectedPriceRange(e.target.value);
    };

    return (
        <div style={{ background: "#3c3c3c", paddingTop: "100px" }}>
            {/* Your Swiper and other sections */}
            <section className='text-white'>
                <div className='container row mt-3'>
                    {/* Category filter */}
                    <div className='col-md-2'>
                        <h6 className='text-center'>
                            Filter By Category
                        </h6>
                        <div className='d-flex flex-column text-white'>
                            {Array.isArray(categories) && categories.map((category, index) => (
                                <Checkbox key={index} onChange={(e) => handleCategoryChange(category._id, e.target.checked)}>
                                    {category.title}
                                </Checkbox>
                            ))}
                        </div>

                        {/* Price filter */}
                        <h6 className='text-center mt-4'>
                            Filter By Price
                        </h6>
                        <div className='d-flex flex-column'>
                            <Radio.Group onChange={handlePriceRangeChange}>
                                {PriceData.map(priceRange => (
                                    <Radio key={priceRange._id} value={priceRange.array}>
                                        {priceRange.name}
                                    </Radio>
                                ))}
                            </Radio.Group>
                        </div>

                        {/* Reset filter button */}
                        <div className='d-flex flex-column mt-4'>
                            <button className='btn btn-danger' onClick={() => window.location.reload()}>
                                RESET FILTER
                            </button>
                        </div>
                    </div>
                    {/* Product list */}
                    <div className='col-md-9 '>
                        <h1 className='text-center'>
                            All Products
                        </h1>
                        <div className='d-flex flex-wrap'>
                            {filteredProducts.map((item, index) => (
                                <Product key={index} item={item} />
                            ))}
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default HomePage;
