import React, { useEffect } from 'react'
import {  Table } from 'antd';
import AdminMenu from '../components/AdminMenu';
import {useDispatch, useSelector} from 'react-redux'
import { getAllUsers } from '../redux/user/userSlice';

const columns = [
  {
    title: 'STT',
    dataIndex: 'key',
  },
  {
    title: 'Name',
    dataIndex: 'name',
    defaultSortOrder:"descend",
    sorter :(a,b)=>a.name.length - b.name.length
  },
  {
    title: 'email',
    dataIndex: 'email',
  },
  {
    title: 'Mobile',
    dataIndex: 'mobile',
  },
  {
    title: 'Address',
    dataIndex: 'address',
  },
];

const ListUsers = () => {
    const dispatch = useDispatch()
    useEffect(()=>{
      dispatch(getAllUsers()) // gọi tới slice -> gọi tới service
    },[])
    const data1=[]
    const customerState = useSelector(state=>state.user.customers) // lấy từ redux 
    for (let i = 0; i < customerState.length; i++) {
      if(customerState[i].role !=="admin"){
        data1.push({
            key: i+1,
            name: customerState[i].name,
            email: customerState[i].email,
            mobile: customerState[i].mobile,
            address: customerState[i].address,
        });
      }
    }
    return (
            <div className='container-fluid p-3' style={{minHeight:"90vh"}}>
                <div className='row'>
                    <div className='col-md-3'>
                        <AdminMenu/>
                    </div>
                    <div className='col-md-9'>
                        <h1 className='text-center'>
                            List Users
                        </h1>
                        <div className="">
                            <Table  columns={columns} dataSource={data1} />
                        </div>
                    </div>
                </div>
            </div>
    );
}

export default ListUsers;
