import React, { useEffect, useState } from 'react'
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation,Autoplay } from 'swiper/modules';
import { Checkbox, Radio } from 'antd';
import { PriceData } from '../utils/PriceData';
import {useDispatch,useSelector} from 'react-redux'
import { getAllCategory } from '../redux/category/categorySlice';
import { getProducts } from '../redux/products/productSlice';

const ListImgSlider = [
    {img : "https://scr.vn/wp-content/uploads/2020/07/T%E1%BA%A3i-h%C3%ACnh-%E1%BA%A3nh-si%C3%AAu-xe-HD-c%E1%BB%B1c-%C4%91%E1%BA%B9p-v%E1%BB%81-m%C3%A1y.jpg"},
    {img : "https://scr.vn/wp-content/uploads/2020/07/%E1%BA%A2nh-si%C3%AAu-xe-Lamborghini-Full-HD.jpg"},
    {img : "https://scr.vn/wp-content/uploads/2020/07/%E1%BA%A2nh-si%C3%AAu-xe-%C4%91%E1%BA%B9p-ch%E1%BA%A5t-l%C6%B0%E1%BB%A3ng-cao.jpg"},
    {img : "https://scr.vn/wp-content/uploads/2020/07/H%C3%ACnh-Si%C3%AAu-xe-Roll-Royce-4k-scaled.jpg"},
  ]
export default function HomePage() {
    const dispatch = useDispatch()
    useEffect(()=>{
        dispatch(getAllCategory())
        dispatch(getProducts())
    },[])
    
    const [check,setCheck] = useState([])
    const [radio,setRadio] = useState([])
    const categories = useSelector(state=>state?.categories?.AllCate)
    const products = useSelector(state=>state?.product?.products)
    console.log(check,radio)
    const handleFilter = (value, id) => {
        let all = [...check];
        if (value) {
          all.push(id);
        } else {
          all = all.filter((c) => c !== id);
        }
        setCheck(all);
    };
    return (
    <div style={{background:"#3c3c3c"}} className='p-4'>
      <section className='pb-4'>
        <div className="container-fluid">
            <div className="row">
                <div className="col-8">
                    <Swiper
                        slidesPerView={1}
                        spaceBetween={30}
                        loop={true}
                        pagination={{
                            clickable: true,
                        }}
                        autoplay={{
                        delay: 3000,
                        disableOnInteraction: false,
                        }}
                        navigation={true}
                        modules={[Navigation,Autoplay]}
                        className="mySwiper"
                    >
                        {
                            ListImgSlider.map((e,index)=>(
                                <SwiperSlide key={index}>
                                    <img style={{borderRadius:"20px"}} width="100%" height="460" src={e.img} alt="sliderbar" />
                                </SwiperSlide>
                            ))
                        }
                    </Swiper>
                </div>
                <div className="col-4">
                    <div className="">
                        <div className="">
                            <img style={{borderRadius:"20px"}} width="100%" height="222" src="https://vanhoadoisong.vn/wp-content/uploads/2022/05/100-hinh-nen-anh-sieu-xe-dep-full-hd-cho-may-tinh-dien-thoai-01.jpg" alt="sliderbar" />
                        </div>
                        <div className="pt-3">
                            <img style={{borderRadius:"20px"}} width="100%" height="222" src="https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2019/6/10/738354/595097.jpg" alt="sliderbar" />
                        </div>
                    </div>
                </div>
            </div>
        </div>
      </section>
      <section className='text-white'>
        <div className='row mt-3'>
                <div className='col-md-2'>
                    <h6 className='text-center'> 
                        Filter By Category
                    </h6>
                    <div className='d-flex flex-column text-white'>
                        {/* {categories?.map(c=>(
                            <Checkbox className='text-white' key={c?._id} onChange={(e)=>handleFilter(e.target.checked,c._id)}>
                                {c?.title}
                            </Checkbox>
                        ))} */}
                        {Array.isArray(categories) && categories.map((i, j) => {
                            return (
                                <Checkbox className='text-white' key={j} onChange={(e)=>handleFilter(e.target.checked,i._id)}>
                                    {i?.title}
                                </Checkbox>
                            )
                        })}
                    </div>

                    {/* aray filter */}
                    <h6 className='text-center mt-4'> 
                        Filter By Price
                    </h6>
                    <div className='d-flex flex-column'>
                        <Radio.Group onChange={e=>setRadio(e.target.value)}>
                            {PriceData?.map(p=>(
                                <div  key={p?._id}>
                                    <Radio className='text-white' value={p.array}>
                                        {p?.name}
                                    </Radio>
                                </div>
                            ))}
                        </Radio.Group>
                    </div>
                    <div className='d-flex flex-column mt-4'>
                        <button className='btn btn-danger' onClick={()=>window.location.reload()}>
                            RESET FILTER
                        </button>
                    </div>
                </div>
                <div className='col-md-9 '>
                    <h1 className='text-center'>
                        All Products
                    </h1>
                    <div className='d-flex flex-wrap'>
                        {products.map(p=>(
                                <div key={p?._id} className='card m-2' style={{width:"18rem"}}>
                                    <img style={{width:"180px",margin:"10px auto 0"}} src={`/api/v1/product/photo-product/${p._id}`} className='card-img-top' alt={p.name} />
                                    <div className='card-body'>
                                        <h5 className='card-title'>{p?.name}</h5>
                                        <p className='card-text'>{p?.price}</p>
                                        <div className='d-flex justify-content-between'>
                                            {/* <button className="btn btn-primary" onClick={()=>navigate(`/product/${p.slug}`)}>More Details</button> */}
                                            {/* <button  className="btn btn-secondary" onClick={()=>{
                                                setCart([...cart, p]);
                                                localStorage.setItem(
                                                  "cart",
                                                  JSON.stringify([...cart, p])
                                                );
                                                toast.success("Item added to cart")
                                            }}>Add to card</button> */}
                                        </div>
                                    </div>
                                </div>
                            ))}
                    </div>
                    {/* <div className='m-2 p-2 text-center'>
                        {products && products.length < total && // nhỏ hơn total thì sẽ hiển thị nút 
                        (
                            <button className='btn btn-warning' onClick={e=>{
                                e.preventDefault()
                                setPage(page+1)
                            }}>
                                {loading ? "Loading ..." : "Loadmore"}
                            </button>
                        )}
                    </div> */}
                </div>
            </div>
      </section>
    </div>
  )
}
